// TOKENIZING MODULE SOURCE#define _CRT_SECURE_NO_WARNINGS // ignoring warnings from IDE#define BUFFER_SIZE 300         // declare a variable for the size of the buffer#include "tokenizing.h"         // includes the header file which contains function prototypes, librarys, structs// VIvoid tokenizing (void) {                 //function of tokenizing, user inputs some words and ir will output each word on a separate lineprintf ("*** Start of Tokenizing Words Demo ***\n");  //print the sentencechar words [BUFFER SIZEl];               //declare the user input ,char variable string and stores itchar* nextword = NULL;                   // declare a char pointer to check the string until the last wordint wordsCounter;                        // declare the int variable to count the number of use input wordsdo                                       // infinite do loop to seperate and count the words until q is entered{printf ("Type a few words separated by space (q - to quit) : \n") ;   //print the sentencefgets (words, BUFFER SIZE,stdin);         //gets the input words from user in stores it in wordswords [strlen (words) - 1] ='\0';         //sets the last character of the input to \0if (strcmp (words,"q") != 0){             // checks if q is intered if not it continues if yes ends the loop   nextword = strtok (words," ");         // check the space and goes for the next word   wordsCounter = 1;                      // sets wordscounter into 1   while (nextword)                       // loop will continue until there is no other words{printf ("Word #%d is \'%s\ '\n", wordsCounter++, nextword); // print the words and number of wordsnextword = strtok (NULL," ");              //when there is no word left to retrieve, strtok returns NULL}while (strcmp (words,"q") != 0) ;         ////checks if string is equal to q is true exits loopprintf("*** End of Tokenizing Words Demo ***\n\n") ; // prints the last sentence}}}